    private void loginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginActionPerformed
        // TODO add your handling code here:

        try{
            String s1=lt1.getText();
            String s2=String.valueOf(lt2.getPassword());

                                               
            checkAdmin.setString(1,s1);
            checkAdmin.setString(2, s2);
            ResultSet r=checkAdmin.executeQuery();
            if(r.next())
            {
                Ruby=random();
                c1l2.setText(Ruby);
                lt1.setText("");
                lt2.setText("");
                jPanel3.removeAll();
                jPanel3.add(success);
                jPanel3.repaint();
                jPanel3.revalidate();

            }
            else
            {
                //JOptionPane.showMessageDialog(null,"Error");
                
                lt1.setText("");
                lt2.setText("");
                c1l2.setText("Hidden Content");
                jPanel3.removeAll();
                jPanel3.add(failure);
                jPanel3.repaint();
                jPanel3.revalidate();
            }
        }
        catch(Exception e){
            JOptionPane.showMessageDialog(null,e);
            e.printStackTrace();
        }
    }//GEN-LAST:event_loginActionPerformed

