    private void rregisterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_rregisterActionPerformed
        // TODO add your handling code here:
        String name=rname.getText();
        String gender="";
        if(rb1.isSelected())
           gender=rb1.getText();
        else if (rb2.isSelected())
           gender=rb2.getText();
        else
            gender=rb3.getText();
        
        java.util.Date date=rcalander.getDate();
        java.sql.Date obj=new java.sql.Date(date.getTime());
        String dob=obj.toString();
        String address=raddress.getText();
        String skills="";
        if(rcb1.isSelected())
            skills=skills+rcb1.getText()+",";
         if(rcb2.isSelected())
            skills=skills+rcb2.getText()+",";
          if(rcb3.isSelected())
            skills=skills+rcb3.getText()+",";
           if(rcb4.isSelected())
            skills=skills+rcb4.getText()+",";
        String country=(String)rcountry.getSelectedItem();
        cname.setText(name);
        cgender.setText(gender);
        cdob.setText(dob);
        caddress.setText(address);
        cskills.setText(skills);
        ccountry.setText(country);
        parent.removeAll();
        parent.add(c3);
        parent.repaint();
        parent.revalidate();
    
    
    }//GEN-LAST:event_rregisterActionPerformed

    private void submitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_submitActionPerformed
        // TODO add your handling code here:
      try{
        String n=cname.getText();
        String g=cgender.getText();
        String d=cdob.getText();
        String a=caddress.getText();
        String s=cskills.getText();
        String c=ccountry.getText();
        addUser.setString(1, n);
        addUser.setString(2, g);
        addUser.setString(3, d);
        addUser.setString(4, a);
        addUser.setString(5, s);
        addUser.setString(6, c);
        addUser.executeUpdate();
        JOptionPane.showMessageDialog(null,"Done");
        rname.setText("");
        buttonGroup1.clearSelection();
        raddress.setText("");
        rcountry.setSelectedIndex(1);
        rcalander.setCalendar(null);
        rcb1.setSelected(false);
        rcb2.setSelected(false);
        rcb3.setSelected(false);
        rcb4.setSelected(false);
        
        cname.setText("");
        cgender.setText("");
        cdob.setText("");
        caddress.setText("");
        cskills.setText("");
        ccountry.setText("");
        parent.removeAll();
        parent.add(c3);
        parent.repaint();
        parent.revalidate();
}
    catch(Exception ex){
         JOptionPane.showMessageDialog(null,ex);
            ex.printStackTrace();
}
    }//GEN-LAST:event_submitActionPerformed

